SELECT CONCAT(empl.FIRST_NAME, " ", empl.LAST_NAME) AS `Nome completo funcionário 1`, empl.SALARY AS `Salário funcionário 1`, empl.PHONE_NUMBER AS `Telefone funcionário 1`, CONCAT(empl2.FIRST_NAME, " ", empl2.LAST_NAME) AS `Nome completo funcionário 2`, empl2.SALARY AS `Salário funcionário 2`, empl2.PHONE_NUMBER AS `Telefone funcionário 2` FROM hr.employees AS empl, hr.employees AS empl2 WHERE empl.JOB_ID = empl2.JOB_ID AND empl2.EMPLOYEE_ID <> empl.EMPLOYEE_ID ORDER BY `Nome completo funcionário 1`, `Nome completo funcionário 2`;