SELECT CONCAT(empl.FIRST_NAME, " ", empl.LAST_NAME) AS `Nome completo`, job.JOB_TITLE AS Cargo, jobhis.START_DATE AS `Data de in√≠cio do cargo"`, depar.DEPARTMENT_NAME AS Departamento FROM hr.employees AS empl INNER JOIN hr.jobs AS job INNER JOIN hr.departments AS depar INNER JOIN hr.job_history AS jobhis ON depar.DEPARTMENT_ID = jobhis.DEPARTMENT_ID AND jobhis.EMPLOYEE_ID = empl.EMPLOYEE_ID AND job.JOB_ID = jobhis.JOB_ID ORDER BY `Nome completo` DESC, Cargo;